import React, { useState } from 'react';
import { carService } from '../services/carService';
import { categoryService } from '../services/categoryService';

const AddCarForm = () => {
    const [formData, setFormData] = useState({
        marque: '',
        modele: '',
        prix: '',
        description: '',
        categorie: null,
        imagePath: ''
    });
    const [categories, setCategories] = useState([]);
    const [selectedFile, setSelectedFile] = useState(null);
    const [previewUrl, setPreviewUrl] = useState('');
    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(true);

    React.useEffect(() => {
        const fetchCategories = async () => {
            try {
                setLoading(true);
                const data = await categoryService.getAllCategories();
                setCategories(data);
                setError(null);
            } catch (err) {
                setError('Failed to load categories. Please try again later.');
                console.error('Error loading categories:', err);
            } finally {
                setLoading(false);
            }
        };
        fetchCategories();
    }, []);

    const handleFileChange = (event) => {
        const file = event.target.files[0];
        if (file) {
            setSelectedFile(file);
            const reader = new FileReader();
            reader.onloadend = () => {
                setPreviewUrl(reader.result);
            };
            reader.readAsDataURL(file);
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            let imagePath = '';
            if (selectedFile) {
                imagePath = await carService.uploadImage(selectedFile);
            }

            const carData = {
                ...formData,
                imagePath,
                prix: parseFloat(formData.prix)
            };

            await carService.addCar(carData);
            // Reset form
            setFormData({
                marque: '',
                modele: '',
                prix: '',
                description: '',
                categorie: null,
                imagePath: ''
            });
            setSelectedFile(null);
            setPreviewUrl('');
            alert('Car added successfully!');
        } catch (error) {
            console.error('Error adding car:', error);
            alert('Failed to add car. Please try again.');
        }
    };

    if (loading) {
        return <div>Loading categories...</div>;
    }

    return (
        <form onSubmit={handleSubmit} className="add-car-form">
            <h2>Add New Car</h2>
            
            {error && <div className="error-message">{error}</div>}
            
            <div className="form-group">
                <label>Image:</label>
                <input
                    type="file"
                    accept="image/*"
                    onChange={handleFileChange}
                />
                {previewUrl && (
                    <div className="image-preview">
                        <img src={previewUrl} alt="Preview" style={{ maxWidth: '200px' }} />
                    </div>
                )}
            </div>

            <div className="form-group">
                <label>Brand:</label>
                <input
                    type="text"
                    value={formData.marque}
                    onChange={(e) => setFormData({ ...formData, marque: e.target.value })}
                    required
                />
            </div>

            <div className="form-group">
                <label>Model:</label>
                <input
                    type="text"
                    value={formData.modele}
                    onChange={(e) => setFormData({ ...formData, modele: e.target.value })}
                    required
                />
            </div>

            <div className="form-group">
                <label>Price:</label>
                <input
                    type="number"
                    value={formData.prix}
                    onChange={(e) => setFormData({ ...formData, prix: e.target.value })}
                    required
                />
            </div>

            <div className="form-group">
                <label>Description:</label>
                <textarea
                    value={formData.description}
                    onChange={(e) => setFormData({ ...formData, description: e.target.value })}
                    required
                />
            </div>

            <div className="form-group">
                <label>Category:</label>
                <select
                    value={formData.categorie?.id || ''}
                    onChange={(e) => {
                        const selectedCategory = categories.find(cat => cat.id === parseInt(e.target.value));
                        setFormData({ ...formData, categorie: selectedCategory });
                    }}
                    required
                    disabled={categories.length === 0}
                >
                    <option value="">Select a category</option>
                    {categories.map(category => (
                        <option key={category.id} value={category.id}>
                            {category.nom}
                        </option>
                    ))}
                </select>
                {categories.length === 0 && !error && (
                    <p className="info-message">No categories available</p>
                )}
            </div>

            <button type="submit" disabled={categories.length === 0}>Add Car</button>
        </form>
    );
};

export default AddCarForm;
