import React, { useState, useEffect } from 'react';
import { carService } from '../services/carService';
import { categoryService } from '../services/categoryService';
import CarGrid from '../components/CarGrid';
import CategoryList from '../components/CategoryList';

const Home = () => {
    const [cars, setCars] = useState([]);
    const [categories, setCategories] = useState([]);
    const [selectedCategory, setSelectedCategory] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const [carsData, categoriesData] = await Promise.all([
                    carService.getAllCars(),
                    categoryService.getAllCategories()
                ]);
                setCars(carsData);
                setCategories(categoriesData);
            } catch (error) {
                console.error('Error fetching data:', error);
            } finally {
                setLoading(false);
            }
        };

        fetchData();
    }, []);

    useEffect(() => {
        const fetchCarsByCategory = async () => {
            if (selectedCategory) {
                setLoading(true);
                try {
                    const carsData = await carService.getCarsByCategory(selectedCategory.id);
                    setCars(carsData);
                } catch (error) {
                    console.error('Error fetching cars by category:', error);
                } finally {
                    setLoading(false);
                }
            } else {
                carService.getAllCars().then(setCars);
            }
        };

        fetchCarsByCategory();
    }, [selectedCategory]);

    if (loading) {
        return <div>Loading...</div>;
    }

    return (
        <div className="home-page">
            <header>
                <h1>Car Catalog</h1>
            </header>
            <div className="content">
                <CategoryList
                    categories={categories}
                    selectedCategory={selectedCategory}
                    onSelectCategory={setSelectedCategory}
                />
                <main>
                    <CarGrid cars={cars} />
                </main>
            </div>
        </div>
    );
};

export default Home;
