const API_URL = 'http://localhost:8080/api';

export const carService = {
    getAllCars: async () => {
        const response = await fetch(`${API_URL}/voitures`);
        return response.json();
    },

    getCarById: async (id) => {
        const response = await fetch(`${API_URL}/voitures/${id}`);
        return response.json();
    },

    addCar: async (car) => {
        const response = await fetch(`${API_URL}/voitures`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(car),
        });
        return response.json();
    },

    updateCar: async (id, car) => {
        const response = await fetch(`${API_URL}/voitures/${id}`, {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(car),
        });
        return response.json();
    },

    deleteCar: async (id) => {
        await fetch(`${API_URL}/voitures/${id}`, {
            method: 'DELETE',
        });
    },

    uploadImage: async (file) => {
        try {
            console.log('Starting image upload:', file.name);
            console.log('File type:', file.type);
            console.log('File size:', file.size);
            
            const formData = new FormData();
            formData.append('file', file);
            
            const uploadUrl = `${API_URL}/api/images/upload`;
            console.log('Sending request to:', uploadUrl);
            
            const response = await fetch(uploadUrl, {
                method: 'POST',
                body: formData
            });
            
            if (!response.ok) {
                const errorText = await response.text();
                console.error('Upload failed with status:', response.status);
                console.error('Error response:', errorText);
                throw new Error(`Failed to upload image: ${errorText}`);
            }
            
            const fileName = await response.text();
            if (!fileName || fileName.trim() === '') {
                throw new Error('Received empty filename from server');
            }
            
            console.log('Upload successful, received filename:', fileName);
            return fileName.trim();
        } catch (error) {
            console.error('Error in uploadImage:', error);
            if (error.message === 'Failed to fetch') {
                console.error('Network error: Could not connect to the server. Is the backend running?');
            }
            throw error;
        }
    }
};
