const API_URL = 'http://localhost:8080/api';

const handleResponse = async (response) => {
    if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
    }
    return await response.json();
};

export const carService = {
    getAllCars: async () => {
        try {
            const response = await fetch(`${API_URL}/voitures`);
            return await handleResponse(response);
        } catch (error) {
            console.error('Error fetching cars:', error);
            throw error;
        }
    },

    getCarsByCategory: async (categoryId) => {
        try {
            const response = await fetch(`${API_URL}/voitures/categorie/${categoryId}`);
            return await handleResponse(response);
        } catch (error) {
            console.error('Error fetching cars by category:', error);
            throw error;
        }
    },

    addCar: async (car) => {
        try {
            const response = await fetch(`${API_URL}/voitures`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(car),
            });
            return await handleResponse(response);
        } catch (error) {
            console.error('Error adding car:', error);
            throw error;
        }
    },

    updateCar: async (id, car) => {
        try {
            const response = await fetch(`${API_URL}/voitures/${id}`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(car),
            });
            return await handleResponse(response);
        } catch (error) {
            console.error('Error updating car:', error);
            throw error;
        }
    },

    deleteCar: async (id) => {
        try {
            const response = await fetch(`${API_URL}/voitures/${id}`, {
                method: 'DELETE',
            });
            if (!response.ok) {
                throw new Error(`HTTP error! status: ${response.status}`);
            }
        } catch (error) {
            console.error('Error deleting car:', error);
            throw error;
        }
    },

    uploadImage: async (file) => {
        try {
            const formData = new FormData();
            formData.append('file', file);
            
            const response = await fetch(`${API_URL}/images/upload`, {
                method: 'POST',
                body: formData
            });
            return await handleResponse(response);
        } catch (error) {
            console.error('Error uploading image:', error);
            throw error;
        }
    }
};
