const API_URL = 'http://localhost:8080/api';

export const carService = {
    getAllCars: async () => {
        const response = await fetch(`${API_URL}/voitures`);
        return response.json();
    },

    getCarsByCategory: async (categoryId) => {
        const response = await fetch(`${API_URL}/voitures/category/${categoryId}`);
        return response.json();
    },

    addCar: async (car) => {
        const response = await fetch(`${API_URL}/voitures`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(car),
        });
        return response.json();
    },

    updateCar: async (id, car) => {
        const response = await fetch(`${API_URL}/voitures/${id}`, {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(car),
        });
        return response.json();
    },

    deleteCar: async (id) => {
        await fetch(`${API_URL}/voitures/${id}`, {
            method: 'DELETE',
        });
    },

    uploadImage: async (file) => {
        try {
            console.log('Uploading image:', file.name);
            // For now, just return a default image path
            // We'll implement actual image upload later
            return 'default-car.jpg';
        } catch (error) {
            console.error('Error handling image:', error);
            // Return a default image path if upload fails
            return 'default-car.jpg';
        }
    }
};
